CREATE TABLE `bioscopen` (
  `id` int UNIQUE NOT NULL AUTO_INCREMENT,
  `bios_naam` varchar(50) NOT NULL,
  `adres` varchar(50) NOT NULL,
  `postcode` varchar(50) NOT NULL,
  `stad` varchar(50) NOT NULL,
  `provincie` varchar(50) NOT NULL,
  `begintijd` datetime NOT NULL,
  `eindtijd` datetime NOT NULL,
  `bereikbaar_auto` text NOT NULL,
  `bereikbaar_ov` text NOT NULL,
  `bereikbaar_fiets` text NOT NULL,
  `rolstoeltoegankelijkheid` enum('0','1') NOT NULL DEFAULT '0',
  `voorwaarden` text NOT NULL,

  PRIMARY KEY(`id`)
);

